import React, { useState } from 'react';
import { Plus, Trash2, Star, Bug, FileText, Download } from 'lucide-react';
import './index.css';

interface Feature {
  id: string;
  title: string;
  description: string;
  imageUrl: string;
  notes: string;
  finalImagePath?: string;
}

interface BugFix {
  id: string;
  title: string;
  description: string;
}

function App() {
  const [features, setFeatures] = useState<Feature[]>([]);
  const [bugFixes, setBugFixes] = useState<BugFix[]>([]);
  const [releaseNotesUrl, setReleaseNotesUrl] = useState('');

  const addFeature = () => {
    const newFeature: Feature = {
      id: Date.now().toString(),
      title: '',
      description: '',
      imageUrl: '',
      notes: ''
    };
    setFeatures([...features, newFeature]);
  };

  const removeFeature = (id: string) => {
    setFeatures(features.filter(feature => feature.id !== id));
  };

  const updateFeature = (id: string, field: keyof Feature, value: string) => {
    setFeatures(features.map(feature => 
      feature.id === id ? { ...feature, [field]: value } : feature
    ));
  };

  const addBugFix = () => {
    const newBugFix: BugFix = {
      id: Date.now().toString(),
      title: '',
      description: ''
    };
    setBugFixes([...bugFixes, newBugFix]);
  };

  const removeBugFix = (id: string) => {
    setBugFixes(bugFixes.filter(bugFix => bugFix.id !== id));
  };

  const updateBugFix = (id: string, field: keyof BugFix, value: string) => {
    setBugFixes(bugFixes.map(bugFix => 
      bugFix.id === id ? { ...bugFix, [field]: value } : bugFix
    ));
  };

  const generateEmailHTML = () => {
    const html = `
<!DOCTYPE html>
<html lang="pt-BR">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>BeeDirect Release Notes</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            line-height: 1.6;
            color: #333333;
            max-width: 600px;
            margin: 0 auto;
            padding: 20px;
            background-color: #F5F5F5;
        }
        .email-container {
            background: #FFFFFF;
            border-radius: 12px;
            padding: 32px;
            box-shadow: 0 4px 12px rgba(0, 47, 108, 0.15);
        }
        .logo-container {
            text-align: center;
            margin-bottom: 32px;
            padding-bottom: 24px;
            border-bottom: 2px solid #F5F5F5;
        }
        .logo {
            max-width: 200px;
            height: auto;
        }
        .section {
            margin-bottom: 32px;
        }
        .section h2 {
            color: #002F6C;
            font-size: 1.4rem;
            margin-bottom: 16px;
            display: flex;
            align-items: center;
            gap: 8px;
        }
        .feature-item, .bug-item {
            background: #F5F5F5;
            border-left: 4px solid #FFD100;
            padding: 16px;
            margin-bottom: 16px;
            border-radius: 0 8px 8px 0;
        }
        .feature-item h3, .bug-item h3 {
            color: #002F6C;
            margin-bottom: 8px;
            font-size: 1.1rem;
        }
        .feature-image {
            max-width: 100%;
            height: auto;
            border-radius: 8px;
            margin: 12px 0;
        }
        .notes {
            background: rgba(255, 209, 0, 0.1);
            border: 1px solid #FFD100;
            border-radius: 6px;
            padding: 12px;
            margin-top: 8px;
            font-size: 0.9rem;
            color: #002F6C;
        }
        .release-notes-link {
            background: #FFD100;
            color: #002F6C;
            padding: 12px 24px;
            text-decoration: none;
            border-radius: 8px;
            display: inline-block;
            font-weight: 500;
            margin-top: 16px;
        }
    </style>
</head>
<body>
    <div class="email-container">
        <div class="logo-container">
            <img src="/images/95A1069C-E971-4EC4-94EA-7E4E1AD79692.png" alt="BeeDirect Logo" class="logo">
        </div>

        ${features.length > 0 ? `
        <div class="section">
            <h2>üåü Novas Funcionalidades</h2>
            ${features.map(feature => `
            <div class="feature-item">
                <h3>${feature.title}</h3>
                <p>${feature.description}</p>
                ${feature.imageUrl ? `<img src="${feature.imageUrl}" alt="${feature.title}" class="feature-image">` : ''}
                ${feature.notes ? `<div class="notes"><strong>Notas:</strong> ${feature.notes}</div>` : ''}
            </div>
            `).join('')}
        </div>
        ` : ''}

        ${bugFixes.length > 0 ? `
        <div class="section">
            <h2>üêõ Corre√ß√µes de Bugs</h2>
            ${bugFixes.map(bugFix => `
            <div class="bug-item">
                <h3>${bugFix.title}</h3>
                <p>${bugFix.description}</p>
            </div>
            `).join('')}
        </div>
        ` : ''}

        ${releaseNotesUrl ? `
        <div class="section">
            <h2>üìã Release Notes Completo</h2>
            <p>Para mais detalhes t√©cnicos e informa√ß√µes completas sobre esta release:</p>
            <a href="${releaseNotesUrl}" class="release-notes-link">Ver Release Notes T√©cnico</a>
        </div>
        ` : ''}
    </div>
</body>
</html>
    `;

    const blob = new Blob([html], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'beedirect-release-email.html';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="container">
      <div className="card">
        <div className="section-header">
          <div className="logo-container">
            <img 
              src="/images/95A1069C-E971-4EC4-94EA-7E4E1AD79692.png" 
              alt="BeeDirect Logo" 
              className="logo"
            />
          </div>
        </div>
        <h1 style={{ textAlign: 'center', color: '#002F6C', marginBottom: '8px' }}>
          Gerador de Email de Release
        </h1>
        <p style={{ textAlign: 'center', color: '#333333', marginBottom: '32px' }}>
          BeeDirect - Omnibees
        </p>
      </div>

      {/* Features Section */}
      <div className="card">
        <div className="section-header">
          <Star className="text-yellow-500" size={24} />
          <h2 className="section-title">Novas Funcionalidades</h2>
        </div>

        {features.map((feature, index) => (
          <div key={feature.id} className="feature-item">
            <div className="item-header">
              <div className="item-number">{index + 1}</div>
              <button 
                onClick={() => removeFeature(feature.id)}
                className="remove-btn"
                title="Remover feature"
              >
                <Trash2 size={16} />
              </button>
            </div>

            <div className="form-group">
              <label className="form-label">T√≠tulo da Feature</label>
              <input
                type="text"
                className="form-input"
                value={feature.title}
                onChange={(e) => updateFeature(feature.id, 'title', e.target.value)}
                placeholder="Ex: Nova funcionalidade de relat√≥rios avan√ßados"
              />
            </div>

            <div className="form-group">
              <label className="form-label">Descri√ß√£o</label>
              <textarea
                className="form-input form-textarea"
                value={feature.description}
                onChange={(e) => updateFeature(feature.id, 'description', e.target.value)}
                placeholder="Descreva a funcionalidade e seus benef√≠cios..."
              />
            </div>

            <div className="form-group">
              <label className="form-label">URL da Imagem (opcional)</label>
              <div className="image-input-container">
                <input
                  type="text"
                  className="form-input"
                  value={feature.imageUrl}
                  onChange={(e) => updateFeature(feature.id, 'imageUrl', e.target.value)}
                  placeholder="Ex: /images/feature.png | https://exemplo.com/imagem.png | deixe vazio"
                />
                <input
                  type="file"
                  accept="image/*"
                  onChange={async (e) => {
                    const file = e.target.files?.[0];
                    if (file) {
                      try {
                        // Cria FormData para upload
                        const formData = new FormData();
                        formData.append('image', file);

                        // Simula upload (em produ√ß√£o seria para um servidor)
                        const reader = new FileReader();
                        reader.onload = (event) => {
                          const imageDataUrl = event.target?.result as string;
                          // Para preview imediato
                          updateFeature(feature.id, 'imageUrl', imageDataUrl);

                          // Salva tamb√©m o caminho final para o email
                          updateFeature(feature.id, 'finalImagePath', `/images/${file.name}`);
                        };
                        reader.readAsDataURL(file);

                        // Instru√ß√£o melhorada
                        alert(`‚úÖ Imagem carregada com sucesso!\n\nüìÅ Arquivo: ${file.name}\nüîó Caminho para email: /images/${file.name}\n\nüí° Dica: Para usar em produ√ß√£o, implemente um endpoint de upload no servidor.`);

                      } catch (error) {
                        alert('‚ùå Erro ao carregar imagem. Tente novamente.');
                      }
                    }
                  }}
                  className="file-input"
                  id={`file-${feature.id}`}
                  style={{ display: 'none' }}
                />
                <button
                  type="button"
                  onClick={() => document.getElementById(`file-${feature.id}`)?.click()}
                  className="btn btn-file"
                  title="Carregar imagem"
                >
                  üì§ Upload
                </button>
              </div>
              <div 
                className="drag-drop-zone"
                onDragOver={(e) => {
                  e.preventDefault();
                  e.currentTarget.classList.add('drag-over');
                }}
                onDragLeave={(e) => {
                  e.preventDefault();
                  e.currentTarget.classList.remove('drag-over');
                }}
                onDrop={(e) => {
                  e.preventDefault();
                  e.currentTarget.classList.remove('drag-over');

                  const files = e.dataTransfer.files;
                  if (files.length > 0) {
                    const file = files[0];
                    if (file.type.startsWith('image/')) {
                      const reader = new FileReader();
                      reader.onload = (event) => {
                        const imageDataUrl = event.target?.result as string;
                        updateFeature(feature.id, 'imageUrl', imageDataUrl);
                        updateFeature(feature.id, 'finalImagePath', `/images/${file.name}`);
                      };
                      reader.readAsDataURL(file);
                      alert(`üéØ Imagem arrastada com sucesso!\n\nüìÅ ${file.name}`);
                    } else {
                      alert('‚ùå Por favor, arraste apenas arquivos de imagem.');
                    }
                  }
                }}
              >
                üñºÔ∏è Arraste uma imagem aqui ou use as op√ß√µes acima
              </div>
              {feature.imageUrl && (
                 <div className="image-preview-container">
                   <img 
                     src={feature.imageUrl} 
                     alt="Preview" 
                     className="image-preview"
                   />
                   <button
                     type="button"
                     onClick={() => {
                       updateFeature(feature.id, 'imageUrl', '');
                       updateFeature(feature.id, 'finalImagePath', '');
                     }}
                     className="btn-remove-image"
                     title="Remover imagem"
                   >
                     ‚ùå
                   </button>
                 </div>
               )}
               <small className="form-help">
                 üí° <strong>3 Op√ß√µes:</strong> 1) Digite caminho (/images/nome.png) ou URL, 2) Clique "Upload", 3) Arraste imagem na √°rea cinza
               </small>
            </div>

            <div className="form-group">
              <label className="form-label">Notas Adicionais (opcional)</label>
              <textarea
                className="form-input form-textarea"
                value={feature.notes}
                onChange={(e) => updateFeature(feature.id, 'notes', e.target.value)}
                placeholder="Impacto, rollout, m√©tricas, etc..."
              />
            </div>
          </div>
        ))}

        <button onClick={addFeature} className="btn btn-secondary">
          <Plus size={16} />
          Adicionar Feature
        </button>
      </div>

      {/* Bug Fixes Section */}
      <div className="card">
        <div className="section-header">
          <Bug className="text-red-500" size={24} />
          <h2 className="section-title">Corre√ß√µes de Bugs</h2>
        </div>

        {bugFixes.map((bugFix, index) => (
          <div key={bugFix.id} className="bug-item">
            <div className="item-header">
              <div className="item-number">{index + 1}</div>
              <button 
                onClick={() => removeBugFix(bugFix.id)}
                className="remove-btn"
                title="Remover corre√ß√£o"
              >
                <Trash2 size={16} />
              </button>
            </div>

            <div className="form-group">
              <label className="form-label">T√≠tulo do Bug</label>
              <input
                type="text"
                className="form-input"
                value={bugFix.title}
                onChange={(e) => updateBugFix(bugFix.id, 'title', e.target.value)}
                placeholder="Ex: Corre√ß√£o no c√°lculo de comiss√µes"
              />
            </div>

            <div className="form-group">
              <label className="form-label">Descri√ß√£o da Corre√ß√£o</label>
              <textarea
                className="form-input form-textarea"
                value={bugFix.description}
                onChange={(e) => updateBugFix(bugFix.id, 'description', e.target.value)}
                placeholder="Descreva o problema que foi corrigido..."
              />
            </div>
          </div>
        ))}

        <button onClick={addBugFix} className="btn btn-secondary">
          <Plus size={16} />
          Adicionar Corre√ß√£o
        </button>
      </div>

      {/* Release Notes Section */}
      <div className="card">
        <div className="section-header">
          <FileText className="text-blue-500" size={24} />
          <h2 className="section-title">Release Notes</h2>
        </div>

        <div className="form-group">
          <label className="form-label">Link para Release Notes T√©cnico</label>
          <input
            type="url"
            className="form-input"
            value={releaseNotesUrl}
            onChange={(e) => setReleaseNotesUrl(e.target.value)}
            placeholder="https://github.com/empresa/projeto/releases/tag/v1.0.0"
          />
        </div>
      </div>

      {/* Preview Section */}
      <div className="preview-section">
        <div className="section-header">
          <h2 className="section-title">Preview do Email</h2>
        </div>

        <div className="email-preview">
          <div className="logo-container">
            <img 
              src="/images/95A1069C-E971-4EC4-94EA-7E4E1AD79692.png" 
              alt="BeeDirect Logo" 
              className="logo"
            />
          </div>

          {features.length > 0 && (
            <div className="email-section">
              <h2><Star size={20} /> Novas Funcionalidades</h2>
              {features.map(feature => (
                <div key={feature.id} className="feature-preview">
                  <h3>{feature.title || 'T√≠tulo da Feature'}</h3>
                  <p>{feature.description || 'Descri√ß√£o da feature...'}</p>
                  {feature.imageUrl && (
                    <img src={feature.imageUrl} alt={feature.title} className="feature-image" />
                  )}
                  {feature.notes && (
                    <div className="notes">
                      <strong>Notas:</strong> {feature.notes}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}

          {bugFixes.length > 0 && (
            <div className="email-section">
              <h2><Bug size={20} /> Corre√ß√µes de Bugs</h2>
              {bugFixes.map(bugFix => (
                <div key={bugFix.id} className="bug-preview">
                  <h3>{bugFix.title || 'T√≠tulo do Bug'}</h3>
                  <p>{bugFix.description || 'Descri√ß√£o da corre√ß√£o...'}</p>
                </div>
              ))}
